import React, { Component } from "react";
import { Map, GoogleApiWrapper, Marker } from "google-maps-react";

export class MapContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      stores: [{ lat: 47.900238, lng: 106.909943 }],
    };
  }

  displayMarkers = () => {
    return this.state.stores.map((store, index) => {
      return (
        <Marker
          key={index}
          id={index}
          position={{
            lat: store.latitude,
            lng: store.longitude,
          }}
          onClick={() => console.log("You clicked me!")}
        />
      );
    });
  };

  render() {
    const mapStyles = {
      width: "100%",
      height: "65%",
    };
    return (
      <div className="contact_map" id="myAdress">
        <Map
          google={this.props.google}
          zoom={17}
          initialCenter={{ lat: 47.900238, lng: 106.909943 }}
          style={mapStyles}
        >
          {this.displayMarkers()}
          <div className="container">
            <div className="event_location">
              <div className="contact_info_item">
                <h6>Venue Infos</h6>
                <p>
                  Mirpur 1620 Collins Street West <br />
                  Victoria 406 New York
                </p>
              </div>
              <div className="contact_info_item">
                <h6>Contact Info</h6>
                <p>
                  <a href="mailto:saasland@gmail.com">saasland@gmail.com</a>
                </p>
                <p>
                  <a href="tel:3024437488">(+096) 302 443 7488</a>
                </p>
              </div>
              <div className="f_social_icon_two">
                <a href="/#">
                  <i className="ti-facebook"></i>
                </a>
                <a href="/#">
                  <i className="ti-twitter-alt"></i>
                </a>
                <a href="/#">
                  <i className="ti-vimeo-alt"></i>
                </a>
                <a href="/#">
                  <i className="ti-pinterest"></i>
                </a>
              </div>
            </div>
          </div>
        </Map>
      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: "AIzaSyBCB4y0SztfLo236n4JECwTvIQd-VecF2Y",
})(MapContainer);
