import React from "react";
import axios from "axios";

const SignInFrom = () => {
  const [Email, setEMail] = React.useState("");
  const [passwordAdmin, setPasswordAdmin] = React.useState("");

  const signIn = async () => {
    const FormData = require("form-data");

    const config = {
      headers: {
        "Content-type": "application/json",
      },
    };

    const form = new FormData();
    form.append("mail", Email);
    form.append("password", passwordAdmin);

    axios.post("192.168.0.119:8000/api/login/", form, {
      headers: form.getHeaders(),
    });
  };

  return (
    <section className="sign_in_area bg_color sec_pad">
      <div className="container">
        <div className="sign_info">
          <div className="row">
            <div className="col-lg-7">
              <div className="login_info">
                <h2 className="f_p f_600 f_size_24 t_color3 mb_40">Sign In</h2>
                <form className="login-form sign-in-form">
                  <div className="form-group text_box">
                    <label className="f_p text_c f_400">Email</label>
                    <input
                      type="text"
                      placeholder="Email Adress"
                      onChange={setEMail}
                    />
                  </div>
                  <div className="form-group text_box">
                    <label className="f_p text_c f_400">Password</label>
                    <input
                      type="password"
                      placeholder="Enter your password"
                      onChange={setPasswordAdmin}
                    />
                  </div>
                  <div className="d-flex justify-content-between align-items-center">
                    <button
                      onSubmit={signIn()}
                      type="submit"
                      className="btn_three"
                    >
                      Sign in
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
export default SignInFrom;
